import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'


var DisplayEmployeeInfo = (employee) => {
  return <div>
    <h1>Employee Details</h1>
    <p>
      <label> Employee ID:  <b>{employee.Id}</b></label>
    </p>
    <p>
      <label> Employee Name: <b>{employee.Name}</b></label>
    </p>

    <p>
      <label> Employee Salary: <b>{employee.Salary}</b></label>
    </p>
    <Department DeptName="" ></Department>
  </div>;

}

var Department = (deptInfo)=>{
  return <div>
    <p>Dept Name is: <b>deptInfo.DeptName</b></p>
    <p>Deept Head is: <b>{deptInfo.headName}</b></p>
  </div> 
  ;
}


const element =  <DisplayEmployeeInfo Id="101" Name="Rahman" Salary="30000">

</DisplayEmployeeInfo>

ReactDOM.render(element, document.getElementById('root'));
