import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'

class Employee extends React.Component{
  state = {counter : 0};

  addEmployee=()=>{
    this.counter = this.counter +1;
  }
  render(){
    return <div>
      <h2>Welcome to Eployee Component...</h2>
      <p>
        <button onClick={this.addEmployee}>Add Empplyee</button>
      </p>
      <p>
        <label> Add Employee Button is Clicked: <b>{this.counter}</b> </label>
      </p>
    </div>;
  }
}

const element = <Employee></Employee>;

ReactDOM.render(element, document.getElementById("root"));
