import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'


class Employee extends React.Component{
  constructor(props){

  }
  render(){
    return <div>
    <h1>Employee Details</h1>
    <p>
      <label> Employee ID:  <b>{this.props.Id}</b></label>
    </p>
    <p>
      <label> Employee Name: <b>{this.props.Name}</b></label>
    </p>

    <p>
      <label> Employee Salary: <b>{this.props.Salary}</b></label>
    </p>
    <h1> Department Information.</h1>
    <Department DeptName={this.props.DeptName} HeadName= {this.props.HeadName} ></Department>
  </div>;
  }
}

class Department extends React.Component{
  render(){
    return <div>
    <p>Dept Name is: <b>{this.props.DeptName}</b></p>
    <p>Deept Head is: <b>{this.props.HeadName}</b></p>
  </div> ;
  }
}



const element =  <Employee Id="101" Name="Rahman" Salary="30000" DeptName="UI Dev" HeadName="Devid">
</Employee>

ReactDOM.render(element, document.getElementById('root'));
